export const FORAGE_MISHAPS = [
  {
    name: `Poisonous`,
    description: `You find one unit of VEGETABLES, but it is poisonous, something you only discover during your next meal. The poison in the VEGETABLES has a Potency of 3.`
  },
  {
    name: `Leeches`,
    description: `Blood sucking leeches bite into your flesh and you suffer one point of damage to Strength. The leeches can be removed by making a successful HEALING roll. If you fail the roll you suffer another point of damage, but manage to get them off anyway.`
  },{
    name: `Sprained ankle`,
    description: `You fall or twist your ankle and suffers a critical injury: To RUN becomes a slow action. (Healing Time: D6 days)`
  },
  {
    name: `Torn clothes`,
    description: `Your clothes are damaged. Your boots break or your robe snags on thorny plants or sharp rocks. You must roll for the effects of cold. Your clothes can be mended by making a successful CRAFTING roll.`
  },
  {
    name: `Savage animal`,
    description: `A wolf, bear or other wild animal feels threatened, and attacks you.`
  },
  {
    name: `Persistent animal`,
    description: `A squirrel, bird or other small animal follows you around and doesn’t leave you alone. The animal causes trouble, described by the GM – it might make a noise at some inappropriate time, eat your food or steal something.`
  }
]
